package Assignments;

public class VowelUsingSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c='e';
		switch(c)
		{
		case 'a':
		case 'A':
		case 'e':
		case 'E':
		case 'i':
		case 'I':
		case 'o':
		case 'O':
		case 'u':
		case 'U':System.out.println("Given alphabet is a vowel");
		break;
		default:System.out.println("Not a vowel");
		break;
		}
		
		

	}

}
